package com.automation.world;

public class TypeCastingDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Implicit Type
		
		int a=15;
		double b=a;
		System.out.println(b);
		
		//Explicit Type-casting
		
		
		double c=25.45;
		int d=(int)c;
		System.out.println(d);
				
				

	}

}
